import React, { useContext, useState, useEffect } from "react";
import { useNavigate } from "react-router-dom";
import Papa from "papaparse";
import DataContext from "../context/DataContext";

function Experiment() {
  const { formData, taskData, setTaskData, setCsvData } = useContext(DataContext);
  const [currentTaskIndex, setCurrentTaskIndex] = useState(0);
  const [startTime, setStartTime] = useState(null);
  const [countdown, setCountdown] = useState(3);
  const [feedbackColor, setFeedbackColor] = useState({});
  const [disableButtons, setDisableButtons] = useState(false);
  const [shuffledOptions, setShuffledOptions] = useState([]);
  const [incorrectOptions, setIncorrectOptions] = useState([]);
  const [attempts, setAttempts] = useState(0); // Track number of attempts
  const navigate = useNavigate();

  const tasks = [
    // Your tasks remain the same
  ];

  useEffect(() => {
    if (countdown > 0) {
      const timer = setTimeout(() => setCountdown(countdown - 1), 1000);
      return () => clearTimeout(timer);
    } else if (startTime === null) {
      setStartTime(Date.now());
    }
  }, [countdown, startTime]);

  useEffect(() => {
    if (tasks && tasks.length > 0) {
      const options = [tasks[currentTaskIndex].identifier, ...tasks[currentTaskIndex].distractors];
      setShuffledOptions(options.sort(() => Math.random() - 0.5));
      setIncorrectOptions([]);
      setAttempts(0); // Reset attempts for the new task
    }
    // eslint-disable-next-line
  }, [currentTaskIndex]);

  const handleOptionClick = (isCorrect, option) => {
    const endTime = Date.now();
    const timeTaken = endTime - startTime;

    if (isCorrect) {
      const isFirstAttempt = attempts === 0;

      setFeedbackColor({ [option]: "green" });
      setDisableButtons(true);

      // Save task data
      setTaskData((prev) => [
        ...prev,
        {
          ...tasks[currentTaskIndex],
          timeTaken,
          isCorrect: isFirstAttempt, // Only correct if first attempt
          attempts: attempts + 1, // Save total attempts
        },
      ]);

      // Proceed to the next task after a short delay
      setTimeout(() => {
        if (currentTaskIndex + 1 < tasks.length) {
          setCurrentTaskIndex(currentTaskIndex + 1);
          setStartTime(Date.now());
          setFeedbackColor({});
          setDisableButtons(false);
        } else {
          generateCsvData();
          navigate("/results");
        }
      }, 1000);
    } else {
      // Increment attempts and record incorrect option
      setAttempts((prev) => prev + 1);
      setIncorrectOptions((prev) => [...prev, option]);
    }
  };

  const generateCsvData = () => {
    const csvContent = Papa.unparse(
      taskData.map((task) => ({
        name: formData.name,
        age: formData.age,
        experience: formData.experience,
        identifier: task.identifier,
        type: task.type,
        timeTaken: task.timeTaken,
        isCorrect: task.isCorrect,
        attempts: task.isCorrect
          ? 1 // Show "1" for correct on first attempt
          : `${task.attempts} (Incorrect on ${task.attempts} attempts)`, // Add detailed attempt info
      }))
    );

    setCsvData(csvContent);
  };

  return (
    <div>
      <header>
        <h1>Experiment</h1>
      </header>
      <main>
        {countdown > 0 ? (
          <div>
            <h1>The experiment will start in:</h1>
            <h1>{countdown}</h1>
          </div>
        ) : currentTaskIndex < tasks.length ? (
          <div>
            <p>
              Identify the corresponding identifier for:
            </p>
            <h1>
              <strong>{tasks[currentTaskIndex].sentence}</strong>
            </h1>
            <div className="card-grid">
              {shuffledOptions.map((option, index) => (
                <button
                  key={index}
                  className="card-button"
                  onClick={() =>
                    handleOptionClick(option === tasks[currentTaskIndex].identifier, option)
                  }
                  style={{
                    backgroundColor:
                      feedbackColor[option] || (incorrectOptions.includes(option) ? "red" : ""),
                    pointerEvents: disableButtons ? "none" : "auto",
                  }}
                >
                  {option}
                </button>
              ))}
            </div>
            <p>Task {currentTaskIndex + 1} of {tasks.length}</p>
          </div>
        ) : (
          <p>Experiment completed!</p>
        )}
      </main>
    </div>
  );
}

export default Experiment;
