import React, { useContext, useState } from 'react';
import Papa from 'papaparse';
import DataContext from '../context/DataContext';

function Experiment() {
  const { formData, taskData, setTaskData } = useContext(DataContext);
  const [currentTaskIndex, setCurrentTaskIndex] = useState(0);
  const [startTime, setStartTime] = useState(null);

  const tasks = [
    // camelCase tasks
    {
      identifier: "moveSouth", // Correct identifier
      type: "camelCase",
      distractors: ["moveSource", "moreSouth", "moverSound"],
      sentence: "move south",
    },
    {
      identifier: "calculateTotal", // Correct identifier
      type: "camelCase",
      distractors: ["calculateSum", "totalCalculation", "sumCalculate"],
      sentence: "calculate total",
    },
    {
      identifier: "userLoginDetails", // Correct identifier
      type: "camelCase",
      distractors: ["userDetails", "loginUserDetails", "userLogins"],
      sentence: "user login details",
    },

    // kebab-case tasks
    {
      identifier: "move-south", // Correct identifier
      type: "kebabCase",
      distractors: ["move-source", "more-south", "mover-sound"],
      sentence: "move south",
    },
    {
      identifier: "calculate-total", // Correct identifier
      type: "kebabCase",
      distractors: ["calculate-sum", "total-calculation", "sum-calculate"],
      sentence: "calculate total",
    },
    {
      identifier: "user-login-details", // Correct identifier
      type: "kebabCase",
      distractors: ["user-details", "login-user-details", "user-logins"],
      sentence: "user login details",
    },
  ];  

  const handleStartTask = () => {
    setStartTime(Date.now());
  };

  const handleCompleteTask = (isCorrect) => {
    const endTime = Date.now();
    const timeTaken = endTime - startTime;

    setTaskData((prev) => [
      ...prev,
      {
        ...tasks[currentTaskIndex],
        timeTaken,
        isCorrect,
      },
    ]);

    if (currentTaskIndex + 1 < tasks.length) {
      setCurrentTaskIndex(currentTaskIndex + 1);
      handleStartTask();
    } else {
      downloadCSV();
    }
  };

  const downloadCSV = () => {
    const csvData = Papa.unparse(
      taskData.map((task) => ({
        name: formData.name,
        age: formData.age,
        experience: formData.experience,
        identifier: task.identifier,
        type: task.type,
        timeTaken: task.timeTaken,
        isCorrect: task.isCorrect,
      }))
    );

    const blob = new Blob([csvData], { type: 'text/csv;charset=utf-8;' });
    const url = URL.createObjectURL(blob);
    const link = document.createElement('a');
    link.setAttribute('href', url);
    link.setAttribute('download', 'experiment_results.csv');
    link.click();
  };

  return (
    <div>
      <header>
        <h1>Experiment</h1>
      </header>
      <main>
        {currentTaskIndex < tasks.length ? (
         <div>
            <p>
             Identify the corresponding identifier for: <strong>{tasks[currentTaskIndex].sentence}</strong>
            </p>
            {[
             tasks[currentTaskIndex].identifier,
             ...tasks[currentTaskIndex].distractors,
            ]
             .sort(() => Math.random() - 0.5) // Randomize order
        .map((option, index) => (
            <button
            key={index}
            onClick={() =>
                handleCompleteTask(option === tasks[currentTaskIndex].identifier)
            }
            >
            {option}
            </button>
        ))}
    </div>
    ) : (
    <p>Experiment completed!</p>
    )}

      </main>
    </div>
  );
}

export default Experiment;
