import React, { useContext, useState, useEffect } from "react";
import { useNavigate } from "react-router-dom";
import Papa from "papaparse";
import DataContext from "../context/DataContext";

function Experiment() {
  const { formData, taskData, setTaskData, setCsvData } = useContext(DataContext);
  const [currentTaskIndex, setCurrentTaskIndex] = useState(0);
  const [startTime, setStartTime] = useState(null);
  const [countdown, setCountdown] = useState(3);
  const [selectedOption, setSelectedOption] = useState(null); // Track user selection
  const navigate = useNavigate();

  const tasks = [
    {
      identifier: "moveSouth",
      type: "camelCase",
      distractors: ["moveSource", "moreSouth", "moverSound"],
      sentence: "move south",
    },
    {
      identifier: "calculateTotal",
      type: "camelCase",
      distractors: ["calculateSum", "totalCalculation", "sumCalculate"],
      sentence: "calculate total",
    },
    {
      identifier: "userLoginDetails",
      type: "camelCase",
      distractors: ["userDetails", "loginUserDetails", "userLogins"],
      sentence: "user login details",
    },
    {
      identifier: "move-south",
      type: "kebabCase",
      distractors: ["move-source", "more-south", "mover-sound"],
      sentence: "move south",
    },
    {
      identifier: "calculate-total",
      type: "kebabCase",
      distractors: ["calculate-sum", "total-calculation", "sum-calculate"],
      sentence: "calculate total",
    },
    {
      identifier: "user-login-details",
      type: "kebabCase",
      distractors: ["user-details", "login-user-details", "user-logins"],
      sentence: "user login details",
    },
  ];

  useEffect(() => {
    if (countdown > 0) {
      const timer = setTimeout(() => setCountdown(countdown - 1), 1000);
      return () => clearTimeout(timer);
    } else if (startTime === null) {
      setStartTime(Date.now());
    }
  }, [countdown, startTime]);

  const handleCompleteTask = (isCorrect) => {
    const endTime = Date.now();
    const timeTaken = endTime - startTime;

    setTaskData((prev) => [
      ...prev,
      {
        ...tasks[currentTaskIndex],
        timeTaken,
        isCorrect,
      },
    ]);

    setTimeout(() => {
      if (currentTaskIndex + 1 < tasks.length) {
        setCurrentTaskIndex(currentTaskIndex + 1);
        setStartTime(Date.now());
        setSelectedOption(null); // Reset selection for the next task
      } else {
        generateCsvData();
        navigate("/results");
      }
    }, 2000); // Wait 2 seconds before moving to the next task
  };

  const generateCsvData = () => {
    const csvContent = Papa.unparse(
      taskData.map((task) => ({
        name: formData.name,
        age: formData.age,
        experience: formData.experience,
        identifier: task.identifier,
        type: task.type,
        timeTaken: task.timeTaken,
        isCorrect: task.isCorrect,
      }))
    );

    setCsvData(csvContent);
  };

  const handleOptionClick = (option) => {
    if (selectedOption === null) {
      const isCorrect = option === tasks[currentTaskIndex].identifier;
      setSelectedOption(option);
      handleCompleteTask(isCorrect);
    }
  };

  return (
    <div>
      <header>
        <h1>Experiment</h1>
      </header>
      <main>
        {countdown > 0 ? (
          <div>
            <p>The experiment will start in:</p>
            <h1>{countdown}</h1>
          </div>
        ) : currentTaskIndex < tasks.length ? (
          <div>
            <p>
              Identify the corresponding identifier for:{" "}
              <strong>{tasks[currentTaskIndex].sentence}</strong>
            </p>
            <div className="card-grid">
              {[tasks[currentTaskIndex].identifier, ...tasks[currentTaskIndex].distractors]
                .sort(() => Math.random() - 0.5)
                .map((option, index) => (
                  <button
                    key={index}
                    className={`card-button ${
                      selectedOption
                        ? option === tasks[currentTaskIndex].identifier
                          ? "correct"
                          : option === selectedOption
                          ? "incorrect"
                          : ""
                        : ""
                    }`}
                    onClick={() => handleOptionClick(option)}
                    disabled={selectedOption !== null} // Disable after selection
                  >
                    {option}
                  </button>
                ))}
            </div>
          </div>
        ) : (
          <p>Experiment completed!</p>
        )}
      </main>
    </div>
  );
}

export default Experiment;
