import React, { useContext, useEffect } from 'react';
import DataContext from '../context/DataContext';
import Papa from 'papaparse';

function downloadCSV(taskData, formData) {
    const csvData = Papa.unparse(
        taskData.map((task) => ({
            age: formData.age,
            experience: formData.experience,
            identifier: task.identifier,
            task: task.task,
            timeTaken: task.timeTaken,
        }))
    );

    const blob = new Blob([csvData], { type: 'text/csv;charset=utf-8;' });
    const url = URL.createObjectURL(blob);

    const participantName = formData.name || 'participant';
    const filename = `experiment_results_${participantName}.csv`;

    const link = document.createElement('a');
    link.setAttribute('href', url);
    link.setAttribute('download', filename);
    document.body.appendChild(link);
    link.click();
    document.body.removeChild(link);
}

// Helper functions to determine if an identifier is camelCase or kebab-case
function isCamelCase(str) {
    return /^[a-z]+([A-Z][a-z]*)+$/.test(str);
}

function isKebabCase(str) {
    return /^[a-z]+(-[a-z]+)+$/.test(str);
}

function ResultPage() {
    const { taskData, formData } = useContext(DataContext);

    useEffect(() => {
        if (taskData && formData) {
            downloadCSV(taskData, formData);
        }
    }, [taskData, formData]);

    // Calculate total and average times in seconds
    const totalOverallTimeMs = taskData.reduce((sum, task) => sum + task.timeTaken, 0);
    const totalOverallTime = (totalOverallTimeMs / 1000).toFixed(2);

    const camelCaseTasks = taskData.filter((task) => isCamelCase(task.identifier));
    const kebabCaseTasks = taskData.filter((task) => isKebabCase(task.identifier));

    const totalCamelCaseTimeMs = camelCaseTasks.reduce((sum, task) => sum + task.timeTaken, 0);
    const totalKebabCaseTimeMs = kebabCaseTasks.reduce((sum, task) => sum + task.timeTaken, 0);

    const avgCamelCaseTime = camelCaseTasks.length
        ? (totalCamelCaseTimeMs / camelCaseTasks.length / 1000).toFixed(2)
        : 'N/A';
    const avgKebabCaseTime = kebabCaseTasks.length
        ? (totalKebabCaseTimeMs / kebabCaseTasks.length / 1000).toFixed(2)
        : 'N/A';

    return (
        <div>
            <header>
                <h1>Experiment Results</h1>
            </header>
            <main>
                <h3>Thank you for participating in the experiment!</h3>
                <h3>Summary</h3>
                <ul>
                    <li>Total time overall: {totalOverallTime} seconds</li>
                    <li>Average time for camelCase tasks: {avgCamelCaseTime} seconds</li>
                    <li>Average time for kebab-case tasks: {avgKebabCaseTime} seconds</li>
                </ul>
            </main>
            <footer>
                <p>Experimentation & Evaluation - 2024</p>
            </footer>
        </div>
    );
}

export default ResultPage;
